$(document).ready(function(){
      
      	
      		
      
      	$('a[href^="#"]').on('click',function (e) {
	    e.preventDefault();

	    var target = this.hash;
	    var $target = $(target);

	    $('html, body').stop().animate({
	        'scrollTop': $target.offset().top
	    }, 1200, 'swing', function () {
	        window.location.hash = target;
	    });
	  });

     $('.bxslider').bxSlider({
          mode: 'fade',
          speed: 1000    
        });

    $("#arrow").click(function(){
          $("#content1").animate({
               opacity: '0'
             },2000);

          $("#content1").attr("disabled", "disabled").off('click');

          $("#content2").animate({  
               left:'5%',
               opacity: '1'
             },2000);
       });
  }); 
